channel.replacePermissionOverwrites({
overwrites: [
  {
     id: message.author.id,
     denied: ['VIEW_CHANNEL'],
  },
],
  reason: 'Needed to change permissions'
});

// Overwrite permissions for a message author
message.channel.overwritePermissions(message.author, {
