  SEND_MESSAGES: null
})
  .then(updated => console.log(updated.permissionOverwrites.get(message.author.id)))
  .catch(console.error);

// Edit a channel
channel.edit({ name: 'new-channel' })
  .then(console.log)
  .catch(console.error);

