channel.clone(undefined, true, false, 'Needed a clone')
  .then(clone => console.log(`Cloned ${channel.name} to make a channel called ${clone.name}`))
  .catch(console.error);

// Delete the channel
channel.delete('Making room for new channels')
  .then(deleted => console.log(`Deleted ${deleted.name} to make room for new channels`))
  .catch(console.error);

// Delete the channel
channel.delete()
  .then(console.log)
  .catch(console.error);

// Set username
client.user.setUsername('discordjs')
  .then(user => console.log(`My new username is ${user.username}`))
  .catch(console.error);

// Set email
client.user.setEmail('bob@gmail.com', 'some amazing password 123')
  .then(user => console.log(`My new email is ${user.email}`))
  .catch(console.error);

// Set password
client.user.setPassword('some new amazing password 456', 'some amazing password 123')
  .then(user => console.log('New password set!'))
  .catch(console.error);

// Set avatar
client.user.setAvatar('./avatar.png')
  .then(user => console.log(`New avatar set!`))
