guildMember.roles.set(['391156570408615936'])
  .then(console.log)
  .catch(console.error);

// Remove all the roles from a member
guildMember.roles.set([])
  .then(member => console.log(`Member roles is now of ${member.roles.cache.size} size`))
  .catch(console.error);

